    /*-------------------------------------------
     SCROLL TO TOP BUTTON ADDED
    ---------------------------------------------*/
    $('body').append('<a id="scroll-btn" class="scroll-btn" href="#"><i class="ti-arrow-up"></i></a>');
    if ($('#scroll-btn').length) {
        var scrollTrigger = 100, // px
            backToTop = function() {
                var scrollTop = $(window).scrollTop();
                if (scrollTop > scrollTrigger) {
                    $('#scroll-btn').addClass('btn-show');
                } else {
                    $('#scroll-btn').removeClass('btn-show');
                }
            };
        backToTop();
        $(window).on('scroll', function() {
            backToTop();
        });
        $('#scroll-btn').on('click', function(e) {
            e.preventDefault();
            $('html,body').animate({
                scrollTop: 0
            }, 500);
        });
    };


  // Main Slider Animation

  $(".slider-wrapper").on("translate.owl.carousel", function(){
        $('.slide-text  h1, .slide-text ul').removeClass('fadeInDown animated').css('opacity', '0');
        $('.slide-text  h2').removeClass('fadeInDown animated').css('opacity', '0');
        $('.slider-content-btn').removeClass('fadeInDown animated').css('opacity', '0');
  });

  $(".slider-wrapper").on("translated.owl.carousel", function(){
        $('.slide-text  h1, .slide-text ul').addClass('fadeInDown animated').css('opacity', '1');
        $('.slide-text  h2').addClass('fadeInDown animated').css('opacity', '1');
        $('.slider-content-btn').addClass('fadeInDown animated').css('opacity', '1');
  });
  
  


